import React from 'react'
import {
    Badge,
    Card,
    CardHeader,
    CardBody,
    Col,
    Media,
    Table,
    Container,
   
    
    Row,
} from "reactstrap";
import DatePicker from "react-datepicker";
import { useState } from "react";
import "react-datepicker/dist/react-datepicker.css";
import { format } from 'date-fns';
import {useEffect } from 'react';
import LoadingScreen from './loading'








var data = []
const rows = []
function formatDate(dateString) {
    let date = new Date(dateString);
    var year = date.getFullYear();
    var month = date.getMonth() + 1;
    var day = date.getDate();
    if (day < 10) {
        day = 0 + "" + day;
    }
    if (month < 10) {
        month = 0 + "" + month;
    }
    return year + '-' + month + '-' + day;

}

const Transiction=()=> {


    const [sdate, setsDate]=useState();
    const [edate, seteDate]=useState();
    const handlstartdate = (e) => {
        console.log(formatDate(e));
        setsDate(e);
      }
      const handlenddate = (e) => {
        seteDate(e);
      }
    const handleApi=()=>{
        const [loading, setLoading] = useState(true)
        useEffect(() => {
            setTimeout(() => setLoading(false), 6000)
          }, [])
        
        
        var token = localStorage.getItem("token");
        if (token !== null || token !== undefined) {
            const myHeaders = new Headers()
            myHeaders.append("Authorization",
                "Bearer " + localStorage.getItem("token"))
            myHeaders.append("Content-Type", "application/json")
            console.log(formatDate(sdate), formatDate(edate));
            var raw = JSON.stringify({
                "endDate": formatDate(edate),
                "startDate": formatDate(sdate),
                "userId": ""
            })
            //  {console.log(endDate,startDate)}
           
            
    
    
    
            var requestOptions = {
                method: "POST",
                headers: myHeaders,
                body: raw,
                redirect: "follow",
            };
            // const [loading, setLoading] = useState(false);
 
            let transactionUrl = "https://uat.softmintdigital.com/reportservice/transaction/transactionreport?pageNo=0&pageSize=20";
            fetch(transactionUrl,
                requestOptions
            )
                .then((response) => response.text())
                .then((result) => {
                    data = null;
                    
                    addList(JSON.parse(result))
                    console.log(JSON.parse(result))
                    // $(document).ready(function () {
                    //     $('#dataTable').triggerHandler();
                    // });
                    // for (let index = 0; index < result.report.length; index++) {
                    //     data.push(result.report[index]);
                    //     console.log(result.report[index], data);
                    //     this.render(result.report);
                    // }
    
                })
                .catch((error) => console.log(error));
    
        } else {
            this.props.history.push("/auth/login")
        }
    }
    
    const addList = (response) => {
        const list = response.report
        rows.length = 0
        for (let i = 0; i < list.length; i++) {
            console.log("Itretion " + i + " Times");
            //    console.log(JSON.parse(response))
            var lst = list[i];
            rows.push(
                <tr>
                    <th scope="row">
                        <Media className="align-items-center">
                            <Media>
                                <span className="mb-0 text-sm">{i + 1}</span>
                            </Media>
                        </Media>
                    </th>
                    <td>{lst.name}</td>
                    <td>
