var isObject = require('isobject');
var hasValues = require('has-values');
var get = require('get-value');
module.exports = function (obj, prop, noZero) {
  if (isObject(obj)) {
    return hasValues(get(obj, prop), noZero);
  }
  return hasValues(obj, prop);
